sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	var where = 0;
	var which = '';
	if (current.text.indexOf('!flip') > -1) {
		where = current.text.indexOf('!flip') + 6;
		which = 'flip';
	} else if (current.text.indexOf('!invert') > -1){
		where = current.text.indexOf('!invert') + 8;
		which = 'invert';
	} else {
		return;
	}

	var flipTable = {
		'a' : '\u0250',
		'b' : 'q',
		'c' : '\u0254', //open o -- from pne
		'd' : 'p',
		'e' : '\u01DD',
		'f' : '\u025F', //from pne
		'g' : '\u0183',
		'h' : '\u0265',
		'i' : '\u0131', //from pne
		'j' : '\u027E',
		'k' : '\u029E',
		//l : '\u0283',
		'm' : '\u026F',
		'n' : 'u',
		'p' : 'd',
		'q' : 'b',
		'r' : '\u0279',
		't' : '\u0287',
		'u' : 'n',
		'v' : '\u028C',
		'w' : '\u028D',
		'y' : '\u028E',
		'.' : '\u02D9',
		'[' : ']',
		'(' : ')',
		'{' : '}',
		'?' : '\u00BF', //from pne
		'!' : '\u00A1',
		"\'" : ',',
		'<' : '>',
		'_' : '\u203E',
		';' : '\u061B',
		'\u203F' : '\u2040',
		'\u2045' : '\u2046',
		'\u2234' : '\u2235',
		'\r' : '\n' 
	};

	var term = current.text.substr(where).trim();
	if (term == '') {
		var send_confusion = new SlackFall().send_chat(current.channel, ':upside_down_face: !flip or !invert *something*', false, '', current.thread_ts, current.server);
		return;
	}
	var end = flipString(term.toLowerCase());
	// 	gs.info(end);
	var send_flip = new SlackFall().send_chat(current.channel, end, false, '', current.thread_ts, current.server);


	function flipString(aString) {
		var last = aString.length - 1;

		var result = new Array(aString.length);
		for (var i = last; i >= 0; --i) {
			var c = aString.charAt(i);
			var r = flipTable[c];
			result[last - i] = r != undefined ? r : c;
		}
		return result.join('');

	}

})(current, previous);
