sys_script.script:
(function executeRule(current, previous /*null when async*/) {

	var regex = /^\!(?:usage|report)/gmi;
	//var regex2 = /^\!report /gmi;
	if (regex.test(current.text)){
		var report_on = current.text.substr(7).trim();
		if (report_on == '') {
			var send_confusion = new SlackFall().send_chat(current.channel, ':upside_down_face: !usage *something*', false, '', current.thread_ts, current.server);
			return;
		}
		var gr = new GlideRecord('x_165574_slackoff_slacker_text_tracker');
		gr.addEncodedQuery('textLIKE'+report_on+'^sys_created_onONLast 7 days@javascript:gs.beginningOfLast7Days()@javascript:gs.endOfLast7Days()');
		gr.addQuery('server',current.server);
		gr.query();
		var send_chat = new SlackFall().send_chat(current.channel, 'The message `' + report_on + '` has appeared ' + gr.getRowCount() + ' times in the channels I\'ve been watching in the last seven days.', false, '', current.thread_ts, current.server);
	}


})(current, previous);
